import React, { useState, useEffect } from 'react'; 
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { FaShoppingCart } from 'react-icons/fa';
import '../eventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams(); // Certifique-se de que a rota está definida como /event/:eventId
  const navigate = useNavigate();
  const location = useLocation();

  const [event, setEvent] = useState(null);
  const [ticketsToBuy, setTicketsToBuy] = useState(1);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    // Carregar os dados do evento do estado ou localStorage
    if (location.state && location.state.event) {
      // Se os dados do evento vierem do estado da navegação, use-os
      setEvent(location.state.event);
    } else {
      // Caso contrário, procure no localStorage
      const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
      const foundEvent = storedEvents.find((e) => e.id === eventId);
      if (foundEvent) {
        setEvent(foundEvent);
      }
    }
  }, [location.state, eventId]);

  if (!event) {
    return <div>Evento não encontrado!</div>;
  }

  const handleBuyTickets = () => {
    if (ticketsToBuy > event.ticketsAvailable) {
      setErrorMessage('Quantidade de ingressos desejada excede a disponível.');
      return;
    }

    const updatedEvent = {
      ...event,
      ticketsAvailable: event.ticketsAvailable - ticketsToBuy,
    };

    setEvent(updatedEvent);

    // Atualizar os eventos no localStorage
    const storedEvents = JSON.parse(localStorage.getItem('events')) || [];
    const updatedEvents = storedEvents.map((e) => (e.id === eventId ? updatedEvent : e));
    localStorage.setItem('events', JSON.stringify(updatedEvents));

    // Navegar para o checkout
    navigate('/checkout', {
      state: {
        event: updatedEvent,
        ticketsToBuy,
        totalPrice: ticketsToBuy * event.ticketPrice,
      },
    });
  };

  return (
    <div className="event-details-page">
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/all-events')}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={event.eventImage} alt="Logo" onClick={() => navigate('/')} />
          </div>
        </div>
      </header>

      {/* Imagem do evento */}
      <div className="event-image-container">
        <img src={event.eventImage} alt={event.eventName} className="event-large-image" />
      </div>

      <div className="event-details-section">
        <div className="event-info-container">
          <div className="event-info">
            <h2>{event.eventName}</h2>
            <p><strong>{event.eventSubject}</strong></p>
            <p>{event.eventDescription}</p>
            <div className="event-more-info">
              <p><strong>Data:</strong> {event.startDate}</p>
              <p><strong>Hora:</strong> {event.startTime}</p>
              <p><strong>Local:</strong> {event.city}, {event.state}</p>
              <p><strong>Preço:</strong> {event.ticketPrice ? `R$${event.ticketPrice}` : 'Gratuito'}</p>
              <p><strong>Ingressos Restantes:</strong> {event.ticketsAvailable}</p>
            </div>
          </div>

          {/* Card de compra */}
          <div className="ticket-box">
            <h3>Comprar Ingressos</h3>
            <div className="ticket-quantity">
              <label htmlFor="ticket-count">Quantos ingressos?</label>
              <input
                type="number"
                id="ticket-count"
                min="1"
                max={event.ticketsAvailable}
                value={ticketsToBuy}
                onChange={(e) => {
                  setErrorMessage('');
                  setTicketsToBuy(Number(e.target.value));
                }}
              />
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            <p><strong>Valor total:</strong> R$ {(ticketsToBuy * event.ticketPrice).toFixed(2)}</p>
            <button onClick={handleBuyTickets} className="buy-ticket-button">
              Comprar Ingresso <FaShoppingCart />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
