import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../producer.css'; // Arquivo CSS para estilização

const Producer = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    locationType: 'local',
    address: '',
    venueName: '',
    zip: '',
    street: '',
    number: '',
    complement: '',
    district: '',
    city: '',
    state: '',
    eventName: '',
    eventImage: null,
    eventSubject: '',
    eventCategory: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      eventImage: e.target.files[0]
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.city.trim()) {
      alert('O campo "Cidade" é obrigatório.');
      return;
    }

    if (!formData.state.trim()) {
      alert('O campo "Estado" é obrigatório.');
      return;
    }

    console.log('Form data:', formData);
    // Adicione a lógica para salvar os dados ou enviá-los para uma API.
  };

  const handleEventSubmit = (e) => {
    e.preventDefault();

    if (!formData.eventName.trim()) {
      alert('O campo "Nome do evento" é obrigatório.');
      return;
    }

    console.log('Event data:', {
      eventName: formData.eventName,
      eventImage: formData.eventImage,
      eventSubject: formData.eventSubject,
      eventCategory: formData.eventCategory
    });
    // Adicione a lógica para salvar os dados ou enviá-los para uma API.
  };

  return (
    <div className="producer-container">
      {/* Header */}
      <header className="producer-header">
        <div className="logo" onClick={() => navigate('/')}>
          <h1>Divulgame</h1>
        </div>
        <nav className="producer-nav">
          <ul>
            <li onClick={() => navigate('/all-events')}>Meus Eventos</li>
            <li onClick={() => navigate('/support')}>Suporte</li>
            <li onClick={() => navigate('/logout')}>Sair</li>
          </ul>
        </nav>
      </header>

      {/* Main Content */}
      <div className="producer-main">
        <h2>Onde o seu evento vai acontecer?</h2>

        <div className="forms-container">
          {/* Formulário de Dados do Local */}
          <div className="form-container">
            <form onSubmit={handleSubmit} className="location-form">
              <div className="form-group">
                <label htmlFor="locationType">Local:</label>
                <select
                  id="locationType"
                  name="locationType"
                  value={formData.locationType}
                  onChange={handleChange}
                >
                  <option value="local">Local</option>
                  <option value="newAddress">Em um novo endereço</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="address">Informe o endereço ou o nome do local do evento:</label>
                <input
                  type="text"
                  id="address"
                  name="address"
                  placeholder="Endereço ou Nome do Local"
                  maxLength="100"
                  value={formData.address}
                  onChange={handleChange}
                  required
                />
                <small>{100 - formData.address.length} caracteres restantes</small>
              </div>

              <div className="form-group">
                <label htmlFor="zip">CEP:</label>
                <input
                  type="text"
                  id="zip"
                  name="zip"
                  placeholder="_____-___"
                  value={formData.zip}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="street">Av./Rua:</label>
                <input
                  type="text"
                  id="street"
                  name="street"
                  value={formData.street}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="number">Número:</label>
                <input
                  type="text"
                  id="number"
                  name="number"
                  value={formData.number}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="complement">Complemento:</label>
                <input
                  type="text"
                  id="complement"
                  name="complement"
                  placeholder="Complemento"
                  maxLength="250"
                  value={formData.complement}
                  onChange={handleChange}
                />
                <small>{250 - formData.complement.length} caracteres restantes</small>
              </div>

              <div className="form-group">
                <label htmlFor="district">Bairro:</label>
                <input
                  type="text"
                  id="district"
                  name="district"
                  value={formData.district}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="city">Cidade:</label>
                <input
                  type="text"
                  id="city"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="state">Estado:</label>
                <input
                  type="text"
                  id="state"
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  required
                />
              </div>

              <button type="submit" className="submit-button">Salvar Local</button>
            </form>
          </div>

          {/* Formulário de Dados do Evento */}
          <div className="form-container">
            <form onSubmit={handleEventSubmit} className="event-form">
              <div className="form-group">
                <label htmlFor="eventName">Nome do evento:</label>
                <input
                  type="text"
                  id="eventName"
                  name="eventName"
                  placeholder="Nome do evento"
                  value={formData.eventName}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="eventImage">Imagem de divulgação (opcional):</label>
                <input
                  type="file"
                  id="eventImage"
                  name="eventImage"
                  accept="image/jpeg, image/gif, image/png"
                  onChange={handleFileChange}
                />
                <small>
                  Clique ou arraste a imagem aqui
                  <br />
                  A dimensão recomendada é de 1600 x 838 (mesma proporção do formato utilizado nas páginas de evento no Facebook).
                  Formato JPEG, GIF ou PNG de no máximo 2MB. Imagens com dimensões diferentes serão redimensionadas.
                </small>
              </div>

              <div className="form-group">
                <label htmlFor="eventSubject">Classifique seu evento - Assunto:</label>
                <select
                  id="eventSubject"
                  name="eventSubject"
                  value={formData.eventSubject}
                  onChange={handleChange}
                >
                  <option value="">Selecione o assunto</option>
                  <option value="music">Música</option>
                  <option value="theatre">Teatro</option>
                  <option value="sports">Esportes</option>
                  <option value="other">Outros</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="eventCategory">Categoria (opcional):</label>
                <input
                  type="text"
                  id="eventCategory"
                  name="eventCategory"
                  placeholder="-- --"
                  value={formData.eventCategory}
                  onChange={handleChange}
                />
              </div>

              <button type="submit" className="submit-button">Salvar Evento</button>
            </form>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="producer-footer">
        <p>&copy; {new Date().getFullYear()} Divulgame. Todos os direitos reservados.</p>
      </footer>
    </div>
  );
};

export default Producer;
