import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaShoppingCart, FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from 'react-icons/fa';
import logo from '../assets/logo.png';
import bannerImage from '../assets/bannerImage.png';
import '../events.css';
import api from '../api/api'; // Configuração do Axios para o backend

const Events = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const [events, setEvents] = useState([]); // Estado para armazenar os eventos

  useEffect(() => {
    const fetchEvents = async () => {
      const token = localStorage.getItem('token'); // Pega o token JWT do localStorage

      if (!token) {
        navigate('/login'); // Redireciona para login caso não esteja autenticado
        return;
      }

      try {
        const response = await api.get('/produtor/produtor', {
          headers: {
            Authorization: `Bearer ${token}`, // Envia o token JWT
          },
        });
        setEvents(response.data || []); // Atualiza os eventos no estado
      } catch (error) {
        console.error('Erro ao buscar eventos:', error);
        alert('Erro ao carregar eventos. Tente novamente.');
        navigate('/login'); // Redireciona para login em caso de erro
      }
    };

    fetchEvents();

    const token = localStorage.getItem('token');
    const storedUserName = localStorage.getItem('userName');
    setIsLoggedIn(!!token);
    setUserName(storedUserName || '');
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    setIsLoggedIn(false);
    setUserName('');
  };

  const EventCard = ({ event }) => {
    return (
      <div className="event-item">
        <img src={event.eventImage} alt={event.eventName} className="event-image" />
        <h2>{event.eventName}</h2>
        <p><strong>Categoria:</strong> {event.eventCategory}</p>
        <p><strong>Assunto:</strong> {event.eventSubject}</p>
        <p><strong>Detalhes:</strong> <span dangerouslySetInnerHTML={{ __html: event.eventDetails }} /></p>
        <p><strong>Data de Início:</strong> {new Date(event.startDate).toLocaleDateString()}</p>
        <p><strong>Hora de Início:</strong> {event.startTime}</p>
        <p><strong>Local:</strong> {event.city}, {event.state}</p>
        <p><strong>Disponibilidade:</strong> {event.availability}</p>
        <p><strong>Nome do Produtor:</strong> {event.producerName}</p>
      </div>
    );
  };

  return (
    <div>
      {/* Header */}
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/create-event')}>Criar Evento</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={logo} alt="Logo" onClick={() => navigate('/')} />
          </div>
          <nav className="menu-right">
            <ul>
              {isLoggedIn ? (
                <>
                  <p className="welcome-text"><strong>Bem-vindo!</strong></p>
                  <li onClick={handleLogout} className="logout-button">Sair</li>
                </>
              ) : (
                <>
                  <li onClick={() => navigate('/login')}>Acessar Conta</li>
                  <li onClick={() => navigate('/register')}>Cadastre-se</li>
                </>
              )}
              <li onClick={() => navigate('/contact')}>Contato</li>
              <li onClick={() => navigate('/cart')} className="cart-icon">
                <FaShoppingCart color="black" size={18} />
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Banner */}
      <div className="banner-section">
        <div className="text-content">
          <h2>Seja um Produtor de Eventos</h2>
          <p>Crie eventos incríveis e comece a vender ingressos para o seu público agora mesmo.</p>
          <button onClick={() => navigate('/register')} className="produtor-button">
            Seja Produtor
          </button>
        </div>
        <div className="image-content">
          <img src={bannerImage} alt="Banner" className="banner-image" />
        </div>
      </div>

      {/* Events Section */}
      <div className="events-container">
        <h1>Eventos Disponíveis</h1>
        {events.length > 0 ? (
          <div className="events-list">
            {events.map((event) => (
              <EventCard key={event.id} event={event} />
