import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Events = () => {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);

  // Buscar produtos da API
  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (error) {
      console.error("Erro ao buscar produtos:", error);
    }
  };

  useEffect(() => {
    const handleUpdate = () => fetchProducts();
    window.addEventListener("updateProducts", handleUpdate);
  
    return () => window.removeEventListener("updateProducts", handleUpdate);
  }, []);
  
  return (
    <div>
      <header className="header">
        <nav>
          <ul>
            <li onClick={() => navigate('/')}>Home</li>
            <li onClick={() => navigate('/add-product')}>Adicionar Produto</li>
            <li onClick={fetchProducts}>Atualizar Produtos</li>
          </ul>
        </nav>
      </header>

      <h2>Lista de Produtos</h2>
      <div className="products-list">
        {products.length === 0 ? (
          <p>Nenhum produto encontrado.</p>
        ) : (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>R$ {product.price}</p>
              <img src={product.imageUrl} alt={product.name} width="100" />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Events;
