// Checkout.js
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../checkout.css';

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { event, ticketsToBuy, totalPrice } = location.state;

  // Estado para armazenar os dados do formulário
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    cardNumber: '',
    cardExpiration: '',
    cardCVC: '',
  });

  const [isFormValid, setIsFormValid] = useState(true);

  // Função para lidar com a mudança nos campos do formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Função para validar o formulário
  const validateForm = () => {
    const { name, email, cardNumber, cardExpiration, cardCVC } = formData;
    return (
      name.trim() !== '' &&
      email.trim() !== '' &&
      cardNumber.trim() !== '' &&
      cardExpiration.trim() !== '' &&
      cardCVC.trim() !== ''
    );
  };

  // Função para confirmar a compra
  const handleConfirmPurchase = () => {
    if (validateForm()) {
      // Aqui, você pode simular o processo de pagamento
      alert('Pagamento realizado com sucesso!');
      navigate('/'); // Redireciona para a página inicial após o pagamento
    } else {
      setIsFormValid(false);
    }
  };

  return (
    <div className="checkout-page">
      <h2>Resumo da Compra</h2>
      
      {/* Resumo da compra */}
      <div className="checkout-summary">
        <p><strong>Evento:</strong> {event.title}</p>
        <p><strong>Data:</strong> {event.date}</p>
        <p><strong>Quantidade de Ingressos:</strong> {ticketsToBuy}</p>
        <p><strong>Total:</strong> R$ {totalPrice.toFixed(2)}</p>
      </div>

      {/* Formulário de pagamento */}
      <div className="checkout-form">
        <h3>Detalhes de Pagamento</h3>
        
        {!isFormValid && <p className="error-message">Por favor, preencha todos os campos!</p>}
        
        <div className="input-group">
          <label htmlFor="name">Nome Completo:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Seu nome completo"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="email">E-mail:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Seu e-mail"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="cardNumber">Número do Cartão:</label>
          <input
            type="text"
            id="cardNumber"
            name="cardNumber"
            value={formData.cardNumber}
            onChange={handleChange}
            placeholder="1234 5678 9876 5432"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="cardExpiration">Data de Expiração:</label>
          <input
            type="text"
            id="cardExpiration"
            name="cardExpiration"
            value={formData.cardExpiration}
            onChange={handleChange}
            placeholder="MM/AA"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="cardCVC">CVC:</label>
          <input
            type="text"
            id="cardCVC"
            name="cardCVC"
            value={formData.cardCVC}
            onChange={handleChange}
            placeholder="123"
            required
          />
        </div>
      </div>

      {/* Botão para confirmar a compra */}
      <div className="checkout-actions">
        <button onClick={handleConfirmPurchase} className="confirm-purchase-button">
          Confirmar Compra
        </button>
      </div>
    </div>
  );
};

export default Checkout;
