import React, { useState, useEffect } from 'react';
import api from '../api/api';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [events, setEvents] = useState([]);
  const [form, setForm] = useState({ title: '', description: '', price: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user) {
      navigate('/login');
      return;
    }

    api.get(`/events/${user.id}`).then((response) => {
      setEvents(response.data);
    });
  }, [navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const user = JSON.parse(localStorage.getItem('user'));
      await api.post('/events', { ...form, userId: user.id });
      alert('Evento criado com sucesso!');
      setForm({ title: '', description: '', price: '' });
    } catch (error) {
      alert('Erro ao criar evento');
    }
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold text-center text-blue-600 mb-8">Dashboard</h1>

      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-4">Criar Evento</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <input
              name="title"
              placeholder="Título"
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              onChange={handleChange}
            />
          </div>
          <div className="mb-4">
            <textarea
              name="description"
              placeholder="Descrição"
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              onChange={handleChange}
            />
          </div>
          <div className="mb-4">
            <input
              name="price"
              placeholder="Preço"
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              onChange={handleChange}
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Criar Evento
          </button>
        </form>
      </div>

      <h2 className="text-xl font-semibold mb-4">Meus Eventos</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map((event) => (
          <div key={event.id} className="bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-xl font-semibold text-blue-600">{event.title}</h3>
            <p>{event.description}</p>
            <p className="text-gray-500">Preço: R$ {event.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
