import axios from 'axios';

// Supondo que você tenha o token armazenado no localStorage ou em algum estado global
const token = localStorage.getItem('authToken');  // Ou o token pode estar em um estado global ou contexto

axios.post('http://localhost:5000/produtor', data, {
  headers: {
    'Authorization': `Bearer ${token}`
  }
})
.then(response => {
  console.log('Evento criado com sucesso:', response);
})
.catch(error => {
  console.error('Erro ao criar evento:', error);
});
