import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaShoppingCart, FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from 'react-icons/fa';
import logo from '../assets/logo.png';
import bannerImage from '../assets/bannerImage.png';
import '../events.css';
import api from '../api/api'; // Certifique-se de configurar o axios corretamente

const Events = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [events, setEvents] = useState([]); // Estado para armazenar os eventos
  const eventsSectionRef = useRef(null);

  useEffect(() => {
    // Busca os eventos do backend
    // Exemplo usando Axios no frontend
const fetchEvents = async (produtorId) => {
  try {
      const response = await axios.get(`http://localhost:5000/produtor/${produtorId}`);
      console.log(response.data);
  } catch (error) {
      console.error('Erro ao buscar eventos:', error);
  }
};

// Certifique-se de passar o ID do produtor como argumento ao chamar fetchEvents
fetchEvents('123'); // Exemplo de ID


    fetchEvents();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    setIsLoggedIn(false);
  };

  const scrollToEvents = () => {
    if (eventsSectionRef.current) {
      eventsSectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleBuyTicket = (eventId) => {
    // Lógica para comprar ingresso (navegar ou realizar ação específica)
    navigate(`/events/${eventId}`);
  };

  return (
    <div>
      {/* Header */}
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/create-event')}>Criar Evento</li>
              <li onClick={scrollToEvents}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={logo} alt="Logo" onClick={() => navigate('/')} />
          </div>
          <nav className="menu-right">
            <ul>
              {isLoggedIn ? (
                <>
                  <p className="welcome-text"><strong>Bem-vindo!</strong></p>
                  <li onClick={handleLogout} className="logout-button">Sair</li>
                </>
              ) : (
                <>
                  <li onClick={() => navigate('/login')}>Acessar Conta</li>
                  <li onClick={() => navigate('/register')}>Cadastre-se</li>
                </>
              )}
              <li onClick={() => navigate('/contact')}>Contato</li>
              <li onClick={() => navigate('/cart')} className="cart-icon">
                <FaShoppingCart color="black" size={18} />
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Banner */}
      <div className="banner-section">
        <div className="text-content">
          <h2>Seja um Produtor de Eventos</h2>
          <p>Crie eventos incríveis e comece a vender ingressos para o seu público agora mesmo.</p>
          <button onClick={() => navigate('/register')} className="produtor-button">Seja Produtor</button>
        </div>
        <div className="image-content">
          <img src={bannerImage} alt="Banner" className="banner-image" />
        </div>
      </div>

      {/* Lista de Eventos */}
      <div className="events-section" ref={eventsSectionRef}>
        {events.length > 0 ? (
          events.map((event) => (
            <div key={event.id} className="event-card">
              <img
                src={event.eventImage || 'default.jpg'}
                alt={event.eventName}
                className="event-image"
              />
              <h3>{event.eventName}</h3>
              <p>{event.eventSubject}</p>
              <div className="event-details">
                <p><strong>Data:</strong> {event.startDate}</p>
                <p><strong>Hora:</strong> {event.startTime}</p>
                <p><strong>Local:</strong> {event.city}, {event.state}</p>
                <p><strong>Preço:</strong> {event.ticketPrice ? `R$${event.ticketPrice}` : 'Gratuito'}</p>
                <p><strong>Ingressos Disponíveis:</strong> {event.ticketQuantity || 'Não especificado'}</p>
              </div>
              <button onClick={() => handleBuyTicket(event.id)} className="buy-ticket-button">
                Comprar Ingresso
              </button>
            </div>
          ))
        ) : (
          <p>Nenhum evento encontrado.</p>
        )}
      </div>

      {/* Footer */}
      <div className="footer">
        <div className="footer-container">
          {/* Footer content */}
        </div>
        <div className="copyright">
          &copy; {new Date().getFullYear()} Divulgame. Todos os direitos reservados.
        </div>
      </div>
    </div>
  );
};

export default Events;
