import React, { useState } from "react";
import axios from "axios";

const ProductForm = ({ fetchProducts }) => {
  const [product, setProduct] = useState({
    nome: "", // Alterado de "name" para "nome" para coincidir com o backend
    preco: "", // Alterado de "price" para "preco"
    imagem: null,
  });

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setProduct({ ...product, imagem: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("nome", product.nome);
    formData.append("preco", product.preco);
    formData.append("imagem", product.imagem);

    try {
      await axios.post("http://localhost:3000/produtos", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      fetchProducts();
    } catch (error) {
      console.error("Erro ao adicionar produto:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="nome" placeholder="Nome do Produto" onChange={handleChange} required />
      <input type="number" name="preco" placeholder="PreÃ§o" onChange={handleChange} required />
      <input type="file" name="imagem" onChange={handleImageChange} required />
      <button type="submit">Adicionar Produto</button>
    </form>
  );
};

export default ProductForm;
