import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../producer.css';

const Producer = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    locationType: 'local',
    address: '',
    venueName: '',
    zip: '',
    street: '',
    number: '',
    complement: '',
    district: '',
    city: '',
    state: '',
    eventName: '',
    eventSubject: '',
    eventCategory: '',
    eventImage: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.size <= 2 * 1024 * 1024) {
      setFormData({
        ...formData,
        eventImage: file
      });
    } else {
      alert('A imagem deve ser no formato JPEG, GIF ou PNG, com no máximo 2MB.');
    }
  };

  const handleSubmitLocation = (e) => {
    e.preventDefault();

    if (!formData.city.trim()) {
      alert('O campo "Cidade" é obrigatório.');
      return;
    }

    if (!formData.state.trim()) {
      alert('O campo "Estado" é obrigatório.');
      return;
    }

    console.log('Dados do local:', formData);
  };

  const handleSubmitEvent = (e) => {
    e.preventDefault();

    if (!formData.eventName.trim()) {
      alert('O campo "Nome do evento" é obrigatório.');
      return;
    }

    console.log('Dados do evento:', formData);
  };

  return (
    <div className="producer-container">
      <header className="producer-header">
        <div className="logo" onClick={() => navigate('/')}>Divulgame</div>
        <nav className="producer-nav">
          <ul>
            <li onClick={() => navigate('/all-events')}>Meus Eventos</li>
            <li onClick={() => navigate('/support')}>Suporte</li>
            <li onClick={() => navigate('/logout')}>Sair</li>
          </ul>
        </nav>
      </header>

      <div className="producer-main">
        <h2>Onde o seu evento vai acontecer?</h2>

        <div className="form-container">
          <form onSubmit={handleSubmitLocation} className="location-form">
            {/* Local Form Fields */}
            {/* ... campos já existentes ... */}
            <button type="submit" className="submit-button">Salvar Local</button>
          </form>
        </div>

        <h2>Informações Básicas</h2>

        <div className="form-container">
          <form onSubmit={handleSubmitEvent} className="basic-info-form">
            <div className="form-group">
              <label htmlFor="eventName">Nome do evento:</label>
              <input
                type="text"
                id="eventName"
                name="eventName"
                placeholder="Nome do Evento"
                maxLength="100"
                value={formData.eventName}
                onChange={handleChange}
                required
              />
              <small>{100 - formData.eventName.length} caracteres restantes</small>
            </div>

            <div className="form-group">
              <label htmlFor="eventImage">Imagem de divulgação (opcional):</label>
              <input
                type="file"
                id="eventImage"
                name="eventImage"
                accept=".jpeg,.jpg,.gif,.png"
                onChange={handleImageUpload}
              />
              <small>A dimensão recomendada é de 1600 x 838 (proporção do Facebook).</small>
              <small>Formato JPEG, GIF ou PNG, com no máximo 2MB.</small>
            </div>

            <div className="form-group">
              <label htmlFor="eventSubject">Classifique seu evento - Assunto:</label>
              <select
                id="eventSubject"
                name="eventSubject"
                value={formData.eventSubject}
                onChange={handleChange}
                required
              >
                <option value="">Selecione o assunto</option>
                <option value="Music">Música</option>
                <option value="Sports">Esportes</option>
                <option value="Education">Educação</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="eventCategory">Categoria (opcional):</label>
              <input
                type="text"
                id="eventCategory"
                name="eventCategory"
                placeholder="Categoria"
                value={formData.eventCategory}
                onChange={handleChange}
              />
            </div>

            <button type="submit" className="submit-button">Salvar Informações Básicas</button>
          </form>
        </div>
      </div>

      <footer className="producer-footer">
        <p>&copy; {new Date().getFullYear()} Divulgame. Todos os direitos reservados.</p>
      </footer>
    </div>
  );
};

export default Producer;
