import React, { useState } from 'react';
import '../producer.css'; // Estilização do formulário

const CreateEvent = () => {
  const [isOnline, setIsOnline] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: null,
    address: {
      local: '',
      address: '',
      venueName: '',
      zipCode: '',
      street: '',
      number: '',
      complement: '',
      neighborhood: '',
      city: '',
      state: '',
    },
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      address: {
        ...formData.address,
        [name]: value,
      },
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setFormData({ ...formData, image: file });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Event Data:', formData);
    alert('Evento criado com sucesso!');
  };

  return (
    <div className="create-event-container">
      <h1>Criação de Evento</h1>
      <form onSubmit={handleSubmit} className="create-event-form">
        {/* Título do Evento */}
        <div className="form-group">
          <label htmlFor="title">Título do Evento</label>
          <input
            type="text"
            id="title"
            name="title"
            maxLength="100"
            placeholder="Digite o título do evento"
            value={formData.title}
            onChange={handleInputChange}
            required
          />
        </div>

        {/* Descrição do Evento */}
        <div className="form-group">
          <label htmlFor="description">Descrição do Evento</label>
          <textarea
            id="description"
            name="description"
            maxLength="250"
            placeholder="Descreva o evento (máximo 250 caracteres)"
            value={formData.description}
            onChange={handleInputChange}
            required
          ></textarea>
        </div>

        {/* Upload de Imagem */}
        <div className="form-group">
          <label htmlFor="image">Foto do Evento</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>

        {/* Evento Online ou Presencial */}
        <div className="form-group">
          <label>Tipo de Evento</label>
          <div>
            <label>
              <input
                type="radio"
                name="eventType"
                value="online"
                checked={isOnline}
                onChange={() => setIsOnline(true)}
              />
              Online
            </label>
            <label>
              <input
                type="radio"
                name="eventType"
                value="presencial"
                checked={!isOnline}
                onChange={() => setIsOnline(false)}
              />
              Presencial
            </label>
          </div>
        </div>

        {/* Endereço do Evento */}
        {!isOnline && (
          <div className="address-fields">
            <h3>Onde o seu evento vai acontecer?</h3>
            <div className="form-group">
              <label htmlFor="venueName">Nome do Local</label>
              <input
                type="text"
                id="venueName"
                name="venueName"
                maxLength="100"
                placeholder="Informe o nome do local"
                value={formData.address.venueName}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="zipCode">CEP</label>
              <input
                type="text"
                id="zipCode"
                name="zipCode"
                maxLength="9"
                placeholder="_____-___"
                value={formData.address.zipCode}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="street">Av./Rua</label>
              <input
                type="text"
                id="street"
                name="street"
                placeholder="Digite o nome da rua"
                value={formData.address.street}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="number">Número</label>
              <input
                type="text"
                id="number"
                name="number"
                placeholder="Número"
                value={formData.address.number}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="complement">Complemento</label>
              <input
                type="text"
                id="complement"
                name="complement"
                maxLength="250"
                placeholder="Ex.: Apartamento, bloco, etc."
                value={formData.address.complement}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="neighborhood">Bairro</label>
              <input
                type="text"
                id="neighborhood"
                name="neighborhood"
                placeholder="Digite o bairro"
                value={formData.address.neighborhood}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="city">Cidade</label>
              <input
                type="text"
                id="city"
                name="city"
                placeholder="Digite a cidade"
                value={formData.address.city}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="state">Estado</label>
              <input
                type="text"
                id="state"
                name="state"
                placeholder="Digite o estado"
                value={formData.address.state}
                onChange={handleAddressChange}
              />
            </div>
          </div>
        )}

        <button type="submit" className="submit-button">Criar Evento</button>
      </form>
    </div>
  );
};

export default CreateEvent;
