import React, { useState } from 'react';
import { FaGoogle, FaFacebookF } from 'react-icons/fa';
import api from '../api/api';
import '../register.css'; // Adicione o CSS personalizado para estilização

const Register = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '', type: '' });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/auth/register', form);
      alert('Usuário registrado com sucesso!');
    } catch (error) {
      alert('Erro ao registrar usuário');
    }
  };

  const handleGoogleLogin = () => {
    alert('Google login ainda não implementado');
  };

  const handleFacebookLogin = () => {
    alert('Facebook login ainda não implementado');
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h2>Crie sua conta</h2>
        <form onSubmit={handleSubmit} className="register-form">
          <input
            className="form-input"
            name="name"
            placeholder="Nome"
            onChange={handleChange}
          />
          <input
            className="form-input"
            name="email"
            placeholder="Email"
            type="email"
            onChange={handleChange}
          />
          <input
            className="form-input"
            name="password"
            placeholder="Senha"
            type="password"
            onChange={handleChange}
          />
          <select name="type" className="form-select" onChange={handleChange}>
            <option value="buyer">Comprador</option>
            <option value="producer">Produtor</option>
          </select>
          <button type="submit" className="form-button">Registrar</button>
        </form>

        <div className="social-login">
          <p>Ou registre-se com</p>
          <div className="social-icons">
            <button className="social-button google" onClick={handleGoogleLogin}>
              <FaGoogle /> Google
            </button>
            <button className="social-button facebook" onClick={handleFacebookLogin}>
              <FaFacebookF /> Facebook
            </button>
          </div>
        </div>

        <p className="login-link">
          Já tem uma conta? <a href="/login">Faça login</a>
        </p>
      </div>
    </div>
  );
};

export default Register;
