import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaShoppingCart, FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from 'react-icons/fa';
import logo from '../assets/logo.png';
import bannerImage from '../assets/bannerImage.png';
import api from '../api/api'; // Certifique-se de configurar o Axios
import '../events.css';

const Events = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const [events, setEvents] = useState([]);

  // Referência para a seção de eventos
  const eventsSectionRef = useRef(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events'); // Ajuste a rota para a sua API
        setEvents(response.data); // Atualize o estado com os eventos retornados
      } catch (error) {
        console.error('Erro ao carregar eventos:', error);
      }
    };

    const token = localStorage.getItem('token');
    const storedUserName = localStorage.getItem('userName');
    setIsLoggedIn(!!token);
    setUserName(storedUserName || '');

    fetchEvents();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userName');
    setIsLoggedIn(false);
    setUserName('');
  };

  const handleBuyTicket = (eventId) => {
    const event = events.find(event => event.id === eventId);
    if (!event) {
      console.error('Evento não encontrado');
      return;
    }
    navigate(`/event/${eventId}`, { state: { event } });
  };

  // Função para rolar até a seção de eventos
  const scrollToEvents = () => {
    if (eventsSectionRef.current) {
      eventsSectionRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div>
      {/* Header */}
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/create-event')}>Criar Evento</li>
              <li onClick={scrollToEvents}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={logo} alt="Logo" onClick={() => navigate('/')} />
          </div>
          <nav className="menu-right">
            <ul>
              {isLoggedIn ? (
                <>
                  <p className="welcome-text"><strong>Bem-vindo!</strong></p>
                  <li onClick={handleLogout} className="logout-button">
                    Sair
                  </li>
                </>
              ) : (
                <>
                  <li onClick={() => navigate('/login')}>Acessar Conta</li>
                  <li onClick={() => navigate('/register')}>Cadastre-se</li>
                </>
              )}
              <li onClick={() => navigate('/contact')}>Contato</li>
              <li onClick={() => navigate('/cart')} className="cart-icon">
                <FaShoppingCart color="black" size={18} />
              </li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Banner */}
      <div className="banner-section">
        <div className="text-content">
          <h2>Seja um Produtor de Eventos</h2>
          <p>Crie eventos incríveis e comece a vender ingressos para o seu público agora mesmo.</p>
          <button onClick={() => navigate('/register')} className="produtor-button">
            Seja Produtor
          </button>
        </div>
        <div className="image-content">
          <img src={bannerImage} alt="Banner" className="banner-image" />
        </div>
      </div>

      {/* Lista de Eventos */}
      <div className="events-section" ref={eventsSectionRef}>
        {events.length > 0 ? (
          events.map((event, index) => (
            <div key={event.id || index} className="event-card">
              <img
                src={event.eventImage ? event.eventImage : 'default.jpg'}
                alt={event.eventName}
                className="event-image"
              />
              <h3>{event.eventName}</h3>
              <p>{event.eventSubject}</p>
              <div className="event-details">
                <p><strong>Data:</strong> {event.startDate}</p>
                <p><strong>Hora:</strong> {event.startTime}</p>
                <p><strong>Local:</strong> {event.city}, {event.state}</p>
                <p>
                  <strong>Preço:</strong> {event.ticketPrice ? `R$${event.ticketPrice}` : 'Gratuito'}
                </p>
                <p><strong>Ingressos Disponíveis:</strong> {event.ticketQuantity || 'Não especificado'}</p>
              </div>
              <button onClick={() => handleBuyTicket(event.id)} className="buy-ticket-button">
                Comprar Ingresso
              </button>
            </div>
          ))
        ) : (
          <p>Nenhum evento encontrado.</p>
        )}
      </div>

      {/* Footer */}
      <div className="footer">
        <div className="footer-container">
          <div className="footer-section">
            <h3>Sobre Nós</h3>
            <p>Organizamos eventos incríveis e conectamos você às melhores experiências.</p>
          </div>
          <div className="footer-section">
            <h3>Links Úteis</h3>
            <ul>
              <li><a href="/about">Sobre</a></li>
              <li><a href="/contact">Contato</a></li>
              <li><a href="/privacy">Política de Privacidade</a></li>
              <li><a href="/terms">Termos de Serviço</a></li>
            </ul>
          </div>
          <div className="footer-section">
