import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../producer.css'; // Arquivo CSS para estilização

const Producer = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    locationType: 'local',
    address: '',
    venueName: '',
    zip: '',
    street: '',
    number: '',
    complement: '',
    district: '',
    city: '',
    state: ''
  });

  const [basicInfo, setBasicInfo] = useState({
    eventName: '',
    subject: '',
    category: '',
    image: null
  });

  const handleLocationChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleBasicInfoChange = (e) => {
    const { name, value } = e.target;
    setBasicInfo({
      ...basicInfo,
      [name]: value
    });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setBasicInfo({ ...basicInfo, image: file });
  };

  const handleLocationSubmit = (e) => {
    e.preventDefault();

    if (!formData.city.trim()) {
      alert('O campo "Cidade" é obrigatório.');
      return;
    }

    if (!formData.state.trim()) {
      alert('O campo "Estado" é obrigatório.');
      return;
    }

    console.log('Local Data:', formData);
    // Adicione a lógica para salvar os dados ou enviá-los para uma API.
  };

  const handleBasicInfoSubmit = (e) => {
    e.preventDefault();

    if (!basicInfo.eventName.trim()) {
      alert('O campo "Nome do evento" é obrigatório.');
      return;
    }

    if (!basicInfo.subject.trim()) {
      alert('O campo "Assunto" é obrigatório.');
      return;
    }

    console.log('Basic Info Data:', basicInfo);
    // Adicione a lógica para salvar os dados ou enviá-los para uma API.
  };

  return (
    <div className="producer-container">
      {/* Header */}
      <header className="producer-header">
        <div className="logo" onClick={() => navigate('/')}>
          <h1>Divulgame</h1>
        </div>
        <nav className="producer-nav">
          <ul>
            <li onClick={() => navigate('/all-events')}>Meus Eventos</li>
            <li onClick={() => navigate('/support')}>Suporte</li>
            <li onClick={() => navigate('/logout')}>Sair</li>
          </ul>
        </nav>
      </header>

      {/* Main Content */}
      <div className="producer-main">
        <h2>Onde o seu evento vai acontecer?</h2>

        {/* Formulário de Dados do Local */}
        <div className="form-container">
          <form onSubmit={handleLocationSubmit} className="location-form">
            <div className="form-group">
              <label htmlFor="locationType">Local:</label>
              <select
                id="locationType"
                name="locationType"
                value={formData.locationType}
                onChange={handleLocationChange}
              >
                <option value="local">Local</option>
                <option value="newAddress">Em um novo endereço</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="address">Informe o endereço ou o nome do local do evento:</label>
              <input
                type="text"
                id="address"
                name="address"
                placeholder="Endereço ou Nome do Local"
                maxLength="100"
                value={formData.address}
                onChange={handleLocationChange}
                required
              />
              <small>{100 - formData.address.length} caracteres restantes</small>
            </div>

            <div className="form-group">
              <label htmlFor="zip">CEP:</label>
              <input
                type="text"
                id="zip"
                name="zip"
                placeholder="_____-___"
                value={formData.zip}
                onChange={handleLocationChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="street">Av./Rua:</label>
              <input
                type="text"
                id="street"
                name="street"
                value={formData.street}
                onChange={handleLocationChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="number">Número:</label>
              <input
                type="text"
                id="number"
                name="number"
                value={formData.number}
                onChange={handleLocationChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="complement">Complemento:</label>
              <input
                type="text"
                id="complement"
                name="complement"
                placeholder="Complemento"
                maxLength="250"
                value={formData.complement}
                onChange={handleLocationChange}
              />
              <small>{250 - formData.complement.length} caracteres restantes</small>
            </div>

            <div className="form-group">
              <label htmlFor="district">Bairro:</label>
              <input
                type="text"
                id="district"
                name="district"
                value={formData.district}
                onChange={handleLocationChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="city">Cidade:</label>
              <input
                type="text"
                id="city"
                name="city"
                value={formData.city}
                onChange={handleLocationChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="state">Estado:</label>
              <input
                type="text"
                id="state"
                name="state"
                value={formData.state}
                onChange={handleLocationChange}
                required
              />
            </div>

            <button type="submit" className="submit-button">Salvar Local</button>
          </form>
        </div>

        {/* Formulário de Informações Básicas */}
        <h2>Informações Básicas</h2>
        <div className="form-container">
          <form onSubmit={handleBasicInfoSubmit} className="basic-info-form">
            <div className="form-group">
              <label htmlFor="eventName">Nome do evento:</label>
              <input
                type="text"
                id="eventName"
                name="eventName"
                placeholder="Nome do evento"
                maxLength="100"
                value={basicInfo.eventName}
                onChange={handleBasicInfoChange}
                required
              />
              <small>{100 - basicInfo.eventName.length} caracteres restantes</small>
            </div>

            <div className="form-group">
              <label htmlFor="image">Imagem de divulgação (opcional):</label>
              <input
                type="file"
                id="image"
                name="image"
                accept="image/jpeg, image/png, image/gif"
                onChange={handleImageUpload}
              />
              <small>
                A dimensão recomendada é de 1600 x 838 (mesma proporção do formato utilizado nas páginas de evento no Facebook). Formato JPEG, GIF ou PNG de no máximo 2MB. Imagens com dimensões diferentes serão redimensionadas.
              </small>
            </div>

            <div className="form-group">
              <label htmlFor="subject">Assunto:</label>
              <select
                id="subject"
                name="subject"
                value={basicInfo.subject}
                onChange={handleBasicInfoChange}
                required
              >
                <option value="">Selecione o assunto</option>
                <option value="music">Música</option>
                <option value="sports">Esportes</option>
                <option value="Education">Educação</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="eventCategory">Categoria (opcional):</label>
              <input
                type="text"
                id="eventCategory"
                name="eventCategory"
                placeholder="Categoria"
                value={formData.eventCategory}
                onChange={handleChange}
              />
            </div>

            <button type="submit" className="submit-button">Salvar Informações Básicas</button>
          </form>
        </div>
      </div>

      <footer className="producer-footer">
        <p>&copy; {new Date().getFullYear()} Divulgame. Todos os direitos reservados.</p>
      </footer>
    </div>
  );
};

export default Producer;
