import React, { useState } from 'react'; 
import '../producerDashboard.css';
import { FaUser, FaCogs, FaSignOutAlt } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom'; // Importar o hook de navegação

const ProducerDashboard = () => {
  const [showRepasseForm, setShowRepasseForm] = useState(false);
  const [showFinanceManagement, setShowFinanceManagement] = useState(false);
  const [repasseData, setRepasseData] = useState(null);

  const navigate = useNavigate();

  const handleRepasseClick = () => {
    setShowFinanceManagement(false);
    setShowRepasseForm(true);
  };

  const handleFinanceManagementClick = () => {
    setShowRepasseForm(false);
    setShowFinanceManagement(true);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const data = Object.fromEntries(formData.entries());
    setRepasseData(data);
    alert('Dados enviados com sucesso!');
    setShowRepasseForm(false);
  };

  const handleBackClick = () => {
    setShowRepasseForm(false);
    setShowFinanceManagement(false);
  };

  return (
    <div className="dashboard-container">
      {/* Barra lateral */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <img 
            src="https://via.placeholder.com/80" // Exemplo de foto do usuário
            alt="User Avatar"
            className="user-avatar"
          />
          <h3>Olá, Usuário!</h3>
        </div>
        <nav className="sidebar-nav">
          <ul>
            <li className="active"><strong>Área do Produtor</strong></li>
            <li><button onClick={handleRepasseClick}><FaUser /> Dados de Repasse</button></li>
            <li><button onClick={handleFinanceManagementClick}><FaCogs /> Gestão Financeira</button></li>
            <li>
              <button onClick={() => navigate('/events')}>
                <FaSignOutAlt /> Sair
              </button>
            </li>
          </ul>
        </nav>
      </aside>

      {/* Conteúdo principal */}
      <div className="dashboard-content">
        {showRepasseForm ? (
          // Seção de Formulário de Repasse
          repasseData ? (
            <section className="repasse-view-section">
              <h2>Dados de Repasse</h2>
              <ul>
                <li><strong>Tipo de Cadastro:</strong> {repasseData.tipoCadastro}</li>
                <li><strong>CPF/CNPJ:</strong> {repasseData.cpfCnpj}</li>
                <li><strong>Nome Completo:</strong> {repasseData.nome}</li>
                <li><strong>Data de Nascimento:</strong> {repasseData.dataNascimento}</li>
                <li><strong>Telefone:</strong> {repasseData.telefone}</li>
                <li><strong>CEP:</strong> {repasseData.cep}</li>
              </ul>
              <button className="edit-button" onClick={() => setRepasseData(null)}>Editar Dados</button>
              <button className="back-button" onClick={handleBackClick}>Voltar</button>
            </section>
          ) : (
            <section className="repasse-form-section">
              <h2>Formulário de Repasse</h2>
              <form onSubmit={handleFormSubmit} className="repasse-form">
                <div className="form-group">
                  <label>Tipo de Cadastro</label>
                  <select name="tipoCadastro" required>
                    <option value="CPF">CPF</option>
                    <option value="CNPJ">CNPJ</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>CPF/CNPJ *</label>
                  <input type="text" name="cpfCnpj" placeholder="___.___.___-__" required />
                  <p className="form-note">Atenção: essa informação não pode ser alterada após o cadastro.</p>
                </div>
                <div className="form-group">
                  <label>Nome Completo *</label>
                  <input type="text" name="nome" placeholder="Conforme seu documento oficial e sem abreviar" required />
                </div>
                <div className="form-group">
                  <label>Data de Nascimento *</label>
                  <input type="date" name="dataNascimento" required />
                </div>
                <div className="form-group">
                  <label>Telefone (Celular) *</label>
                  <input type="text" name="telefone" placeholder="(__) _____-____" required />
                </div>
                <div className="form-group">
                  <label>CEP *</label>
                  <input type="text" name="cep" placeholder="_____-___" required />
                </div>
                <button type="submit" className="submit-button">Enviar</button>
                <button type="button" className="cancel-button" onClick={handleBackClick}>Cancelar</button>
              </form>
              <button className="back-button" onClick={handleBackClick}>Voltar</button>
            </section>
          )
        ) : showFinanceManagement ? (
          // Seção de Gestão Financeira
          <section className="finance-management-section">
            <h2>Gestão Financeira</h2>
            <p>Monitoramento das finanças:</p>
            <ul>
              <li>Saldo Atual: R$ 5.000,00</li>
              <li>Receitas do Mês: R$ 15.000,00</li>
              <li>Despesas do Mês: R$ 10.000,00</li>
            </ul>
            <button className="back-button" onClick={handleBackClick}>Voltar</button>
          </section>
        ) : (
          // Conteúdo padrão do dashboard
          <>
            <header className="dashboard-header">
              <h1>Boa noite, Produtor!</h1>
              <p>Já publicou seu evento?</p>
            </header>

            <section className="action-section">
              <div className="action-box">
                <h2>Criar Evento</h2>
                <button 
                  className="action-button" 
                  onClick={() => navigate('/create-event')}
                >
                  Criar Evento Presencial
                </button>
               
                <button className="action-button">Criar Evento Online</button>
              </div>
              <div className="action-box">
                <h2>Meus Conteúdos</h2>
                <div className="events-list">
                  <p><strong>Meus Eventos:</strong></p>
                  <ul>
                    <li>Evento 1</li>
                    <li>Evento 2</li>
                    <li>Evento 3</li>
                  </ul>
                </div>
              </div>
            </section>
          </>
        )}
      </div>

      <button className="help-button">?</button>
    </div>
  );
};

export default ProducerDashboard;
