import React, { useState, useEffect } from 'react'; 
import { useParams, useLocation } from 'react-router-dom'; 
import { FaShoppingCart } from 'react-icons/fa'; 
import event1 from '../assets/event1.jpg';
import event2 from '../assets/event2.jpg';
import event3 from '../assets/event3.jpg';
import '../eventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams(); // Obtém o ID do evento da URL
  const { state } = useLocation(); // Obtém os dados passados através de 'state'
  const [event, setEvent] = useState(state?.event || null); // Usa o 'state' ou busca do 'localStorage' se necessário

  // Caso o evento não tenha sido encontrado, tente buscar no 'localStorage'
  useEffect(() => {
    if (!event) {
      const savedEvent = localStorage.getItem('eventData');
      if (savedEvent) {
        setEvent(JSON.parse(savedEvent));
      }
    }
  }, [event]);

  const [ticketsToBuy, setTicketsToBuy] = useState(1);
  const [ticketsAvailable, setTicketsAvailable] = useState(event ? event.ticketsAvailable : 0);

  if (!event) {
    return <div>Evento não encontrado!</div>;
  }

  const totalPrice = ticketsToBuy * event.price;

  const handleBuyTickets = () => {
    if (ticketsToBuy <= ticketsAvailable) {
      setTicketsAvailable(ticketsAvailable - ticketsToBuy);
      // Redirecionar para a tela de checkout passando os dados do evento e da compra
      navigate('/checkout', {
        state: {
          event,
          ticketsToBuy,
          totalPrice,
        }
      });
    } else {
      alert('Quantidade de ingressos desejada excede a quantidade disponível!');
    }
  };

  return (
    <div className="event-details-page">
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/all-events')}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={event.image} alt="Logo" onClick={() => navigate('/')} />
          </div>
        </div>
      </header>

      <div className="event-image-container">
        <img src={event.image} alt={event.title} className="event-large-image" />
      </div>

      <div className="event-details-section">
        <div className="event-info-container">
          <div className="event-info">
            <h2>{event.title}</h2>
            <p><strong>{event.subtitle}</strong></p>
            <p>{event.description}</p>
            <div className="event-more-info">
              <p><strong>Data:</strong> {event.date}</p>
              <p><strong>Hora:</strong> {event.time}</p>
              <p><strong>Local:</strong> {event.location}</p>
              <p><strong>Preço:</strong> {`R$ ${event.price.toFixed(2)}`}</p>
              <p><strong>Ingressos Restantes:</strong> {ticketsAvailable}</p>
            </div>
          </div>

          <div className="ticket-box">
            <h3>Comprar Ingressos</h3>
            <div className="ticket-quantity">
              <label htmlFor="ticket-count">Quantos ingressos?</label>
              <input
                type="number"
                id="ticket-count"
                min="1"
                max={ticketsAvailable}
                value={ticketsToBuy}
                onChange={(e) => setTicketsToBuy(Number(e.target.value))}
              />
            </div>
            <p><strong>Ingressos disponíveis:</strong> {ticketsAvailable}</p>
            <p><strong>Valor total:</strong> R$ {totalPrice.toFixed(2)}</p>
            <button onClick={handleBuyTickets} className="buy-ticket-button">
              Comprar Ingresso <FaShoppingCart />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
