import React, { useState, useEffect } from 'react'; 
import { useParams, useNavigate, useLocation } from 'react-router-dom'; 
import { FaShoppingCart } from 'react-icons/fa'; 
import '../eventDetails.css'; 

const EventDetails = () => {
  const { eventId } = useParams(); // Pega o ID do evento da URL
  const navigate = useNavigate();
  const location = useLocation(); // Hook para pegar o estado passado via navegação
  const [event, setEvent] = useState(null); // Estado para armazenar os dados do evento
  const [ticketsToBuy, setTicketsToBuy] = useState(1);
  const [ticketsAvailable, setTicketsAvailable] = useState(0);

  useEffect(() => {
    // Verifica se os dados do evento foram passados via navegação
    if (location.state && location.state.event) {
      const event = location.state.event;
      setEvent(event);
      setTicketsAvailable(event.ticketsAvailable);
    }
  }, [location]); // Dependendo do estado de location, atualiza o evento

  if (!event) {
    return <div>Evento não encontrado!</div>;
  }

  const totalPrice = ticketsToBuy * event.ticketPrice;

  const handleBuyTickets = () => {
    if (ticketsToBuy <= ticketsAvailable) {
      setTicketsAvailable(ticketsAvailable - ticketsToBuy);
      // Redireciona para a tela de checkout com os dados necessários
      navigate('/checkout', {
        state: {
          event,
          ticketsToBuy,
          totalPrice,
        }
      });
    } else {
      alert('Quantidade de ingressos desejada excede a quantidade disponível!');
    }
  };

  return (
    <div className="event-details-page">
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/all-events')}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={event.eventImage} alt="Logo" onClick={() => navigate('/')} />
          </div>
        </div>
      </header>

      {/* Imagem do evento no topo da página */}
      <div className="event-image-container">
        <img src={event.eventImage} alt={event.eventName} className="event-large-image" />
      </div>

      <div className="event-details-section">
        <div className="event-info-container">
          <div className="event-info">
            {/* Descrição e informações do evento */}
            <h2>{event.eventName}</h2>
            <p><strong>{event.eventSubject}</strong></p>
            <p>{event.eventDescription}</p>
            <div className="event-more-info">
              <p><strong>Data:</strong> {event.startDate}</p>
              <p><strong>Hora:</strong> {event.startTime}</p>
              <p><strong>Local:</strong> {event.city}, {event.state}</p>
              <p><strong>Preço:</strong> {event.ticketPrice ? `R$ ${event.ticketPrice}` : 'Gratuito'}</p>
              <p><strong>Ingressos Restantes:</strong> {ticketsAvailable}</p>
            </div>
          </div>

          {/* Card de compra de ingressos */}
          <div className="ticket-box">
            <h3>Comprar Ingressos</h3>
            <div className="ticket-quantity">
              <label htmlFor="ticket-count">Quantos ingressos?</label>
              <input
                type="number"
                id="ticket-count"
                min="1"
                max={ticketsAvailable}
                value={ticketsToBuy}
                onChange={(e) => setTicketsToBuy(Number(e.target.value))}
              />
            </div>
            <p><strong>Ingressos disponíveis:</strong> {ticketsAvailable}</p>
            <p><strong>Valor total:</strong> R$ {totalPrice.toFixed(2)}</p>
            <button onClick={handleBuyTickets} className="buy-ticket-button">
              Comprar Ingresso <FaShoppingCart />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
