import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaShoppingCart } from 'react-icons/fa';
import event1 from '../assets/event1.jpg';
import event2 from '../assets/event2.jpg';
import event3 from '../assets/event3.jpg';
import '../eventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams(); // Pega o ID do evento da URL
  const navigate = useNavigate();

  const events = [
    {
      id: 1,
      title: 'Festival de Música',
      subtitle: 'Aproveite os melhores artistas ao vivo',
      date: '15/01/2025',
      time: '18:00',
      location: 'Parque da Cidade',
      price: 15, // Preço em números
      image: event1,
      description: 'Uma descrição detalhada sobre o evento. Venha se divertir com os melhores artistas.',
      ticketsAvailable: 200 // Quantidade de ingressos restantes
    },
    {
      id: 2,
      title: 'Conferência Tech',
      subtitle: 'Explore as inovações tecnológicas',
      date: '20/02/2025',
      time: '09:00',
      location: 'Centro de Convenções',
      price: 3,
      image: event2,
      description: 'Participe da conferência para descobrir novas tecnologias e fazer networking.',
      ticketsAvailable: 150
    },
    {
      id: 3,
      title: 'Stand-Up Comedy',
      subtitle: 'Uma noite de risadas garantidas',
      date: '10/03/2025',
      time: '20:00',
      location: 'Teatro Municipal',
      price: 8,
      image: event3,
      description: 'Venha rir com os melhores comediantes em uma noite inesquecível.',
      ticketsAvailable: 100
    }
  ];

  // Encontre o evento com base no ID
  const event = events.find(event => event.id === parseInt(eventId));

  // O hook useState deve ser chamado antes de qualquer retorno condicional.
  const [ticketsToBuy, setTicketsToBuy] = useState(1);
  const [ticketsAvailable, setTicketsAvailable] = useState(event.ticketsAvailable);

  // Verifique se o evento não foi encontrado, e só retorne depois de chamar o useState
  if (!event) {
    return <div>Evento não encontrado!</div>;
  }

  // Calcular o valor total dos ingressos
  const totalPrice = ticketsToBuy * event.price;

  const handleBuyTickets = () => {
    if (ticketsToBuy <= ticketsAvailable) {
      setTicketsAvailable(ticketsAvailable - ticketsToBuy);
      alert(`Você comprou ${ticketsToBuy} ingressos para o evento!`);
  
      // Redirecionar para a tela de checkout passando os dados do evento e da compra
      navigate('/checkout', {
        state: {
          event,
          ticketsToBuy,
          totalPrice,
        }
      });
    } else {
      alert('Quantidade de ingressos desejada excede a quantidade disponível!');
    }
  };
  

  return (
    <div className="event-details-page">
      <header className="header">
        <div className="container">
          <nav className="menu-left">
            <ul>
              <li onClick={() => navigate('/')}>Home</li>
              <li onClick={() => navigate('/all-events')}>Todos os Eventos</li>
            </ul>
          </nav>
          <div className="logo">
            <img src={event.image} alt="Logo" onClick={() => navigate('/')} />
          </div>
        </div>
      </header>

      {/* Imagem de evento no topo da tela */}
      <div className="event-image-container">
        <img src={event.image} alt={event.title} className="event-large-image" />
      </div>

      <div className="event-details-section">
        <div className="event-info-container">
          {/* Descrição e informações do evento */}
          <div className="event-info">
            <h2>{event.title}</h2>
            <p><strong>{event.subtitle}</strong></p>
            <p>{event.description}</p>
            <div className="event-more-info">
              <p><strong>Data:</strong> {event.date}</p>
              <p><strong>Hora:</strong> {event.time}</p>
              <p><strong>Local:</strong> {event.location}</p>
              <p><strong>Preço:</strong> {`R$ ${event.price.toFixed(2)}`}</p>
              <p><strong>Ingressos Restantes:</strong> {ticketsAvailable}</p>
            </div>
          </div>

          {/* Card de compra de ingressos */}
          <div className="ticket-box">
            <h3>Comprar Ingressos</h3>
            <div className="ticket-quantity">
              <label htmlFor="ticket-count">Quantos ingressos?</label>
              <input
                type="number"
                id="ticket-count"
                min="1"
                max={ticketsAvailable}
                value={ticketsToBuy}
                onChange={(e) => setTicketsToBuy(Number(e.target.value))}
              />
            </div>
            <p><strong>Ingressos disponíveis:</strong> {ticketsAvailable}</p>
            <p><strong>Valor total:</strong> R$ {totalPrice.toFixed(2)}</p>
            <button onClick={handleBuyTickets} className="buy-ticket-button">
              Comprar Ingresso <FaShoppingCart />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventDetails;
